INSERT INTO `joomlaschulung_overrider` VALUES(134, 'COM_FINDER_ADVANCED_TIPS', '<p>Einige Beispiele zur Benutzung der Suche mit logischen Operatoren:</p><p>Die Eingabe von <span class="term">&bdquo;dies und das&ldquo;</span> in das Suchfeld zeigt alle Ergebnisse, die beide Wörter, &bdquo;dies&ldquo; und &bdquo;das&ldquo; enthalten.</p><p>Die Eingabe von <span class="term">&bdquo;dies nicht das&ldquo;</span> zeigt alle Ergebnisse, die &bdquo;dies&ldquo;, aber nicht &bdquo;das&ldquo; enthalten.</p><p>Die Eingabe von <span class="term">&bdquo;dies oder das&ldquo;</span> zeigt alle Ergebnisse, die entweder &bdquo;dies&ldquo; oder &bdquo;das&ldquo; enthalten.</p><p>Die Eingabe von <span class="term">"dies und das"</span> (mit Anführungszeichen) zeigt alle Ergebnisse, die genau die Wortfolge "dies und das" enthalten.</p>', '/Users/saranasereddin/git/workshop_blank_temp/language/de-DE/de-DE.com_finder.ini');
